import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Router} from "@angular/router";
@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css']
})
export class OrdersComponent implements OnInit{
  public orders : any;

  constructor(private http : HttpClient, private router : Router) {
  }
  ngOnInit() {
    this.http.get("http://localhost:8086/api/orders").subscribe({
      next : orders => {
        this.orders = orders;
      },
      error : err => {
        console.log(err);
      }
    })
  }

  getOrderDetails(o:any) {
    this.router.navigateByUrl("/order-details/"+o.id);
  }
}
<div class="p-3">
  <div class="card">
    <div class="card-header">Orders</div>
    <div class="card-body">
      <h2>Orders</h2>
      <table class="table">
        <thead>
        <tr>
          <th>ID</th> <th>Date</th> <th>State</th> <th>Products Count</th>
        </tr>
        </thead>
        <tbody>
        <tr *ngFor="let o of orders">
          <td>{{o.id}}</td>
          <td>{{o.date}}</td>
          <td>{{o.state}}</td>
          <td>{{o.productItems.length}}</td>
          <td>
            <button class="btn btn-success" (click)="getOrderDetails(o)">Order Details</button>
          </td>
        </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
