import { Component } from '@angular/core';
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-customers',
  templateUrl: './customers.component.html',
  styleUrls: ['./customers.component.css']
})
export class CustomersComponent {
  public  customers : any;
  constructor(private http : HttpClient) {
  }
  ngOnInit() {
    this.http.get("http://localhost:8085/api/customers").subscribe({
      next : data => {
        this.customers = data;
      },
      error : err => {
        console.log(err);
      }
    })
  }
}
<div class="p-3">
  <div class="card">
    <div class="card-header">Customers
      <h2>Products</h2>
      <table class="table">
        <thead>
        <tr>
          <th>ID</th> <th>Name</th> <th>Price</th> <th>Quantity</th>
        </tr>
        </thead>
        <tbody>
        <tr *ngFor="let p of customers">
          <td>{{p.id}}</td>
          <td>{{p.firstName}}</td>
          <td>{{p.lastName}}</td>
          <td>{{p.email}}</td>
        </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
